mapper.select:
SELECT

FROM event_receive_table
WHERE msgId = #{msgId} AND listenerName = #{listenerName} and messageMode = #{messageMode} and consumerGroup = #{consumerGroup}
      and topic = #{topic} and tag = #{tag}

mapper.update:
INSERT INTO event_receive_table(topic, tag, eventStatus,processTime,
lastModifyTime,retryTime,modifyNumber,message,msgId,createTime,consumerGroup,messageMode,listenerName) VALUES (
#{topic},#{tag},#{eventStatus},#{processTime},#{lastModifyTime},
#{retryTime},#{modifyNumber},#{message},#{msgId},#{createTime},#{consumerGroup},#{messageMode},#{listenerName}
)

mapper.select:
SELECT

FROM event_receive_table
WHERE createTime < #{createTime} AND eventStatus = #{eventStatus}

SELECT

FROM event_receive_table
WHERE msgId = #{msgId} AND eventStatus = #{eventStatus}

mapper.update:
UPDATE event_receive_table
eventStatus = #{eventStatus,jdbcType=VARCHAR}, modifyNumber = modifyNumber + 1, processTime = #{processTime,jdbcType=TIMESTAMP}, lastModifyTime = #{processTime,jdbcType=TIMESTAMP},
WHERE msgId = #{msgId,jdbcType=INTEGER} AND listenerName = #{listenerName} and messageMode = #{messageMode} and consumerGroup = #{consumerGroup} and topic = #{topic} and tag = #{tag}
